INSERT INTO `risalah_editor` VALUES (21, 'Sub 1.1', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p>Terdapat beberapa permasalahan unutuk mendukung KPI Manajemen seperti table dibawah ini.&nbsp;</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Tabel 2.Permasalahan dan Analisa Dampak&nbsp;</p>\r\n<p><img src=\"https://cip.kamil.co.id/assets/upload/MjAxOS0wNi0xOCAxMDo1NzowNTE2MzI0NzgwMDA.png\" alt=\"\" width=\"613\" height=\"247\" /></p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Gambar 1.Matriks Resiko.&nbsp;</p>\r\n</body>\r\n</html>', '002/CIP-PTG/2019', 3, NULL);
INSERT INTO `risalah_editor` VALUES (22, 'Sub 1.2', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p><span style=\"font-size: 10.0pt; line-height: 107%; font-family: \'Arial\',sans-serif; mso-fareast-font-family: Arial;\"><span style=\"font: 7.0pt \'Times New Roman\';\">&nbsp;<strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 10.0pt; line-height: 107%; font-family: \'Arial\',sans-serif;\">1.2&nbsp;</span></strong>&nbsp;</span></span><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 10.0pt; line-height: 107%; font-family: \'Arial\',sans-serif;\">PENETAPAN ISU/TEMA DAN KOMPLEKSITAS MASALAH</span></strong></p>\r\n<ul style=\"list-style-type: square;\">\r\n<li class=\"MsoNormal\" style=\"margin-left: .25in; text-align: justify;\"><span style=\"font-size: 10pt; line-height: 107%; font-family: Arial, sans-serif;\">Bila masalah lebih dari satu, pemilihan masalah dilakukan dengan lakukan analisis prioritas (gunakan tools : misal <strong>pareto</strong>) atau dengan justifikasi </span></li>\r\n<li class=\"MsoNormal\" style=\"margin-left: .25in; text-align: justify;\"><span style=\"font-size: 10pt; line-height: 107%; font-family: Arial, sans-serif;\">Untuk justifikasi, tunjukkan KPI, Arahan Manajemen, Notulen Rapat dll sebagai justifikasi pemilihan isu/masalah/kejadian/fakta yang akan diselesaikan. Lampirkan dokumen pendukung.</span></li>\r\n<li class=\"MsoNormal\" style=\"margin-left: .25in; text-align: justify;\"><span style=\"font-size: 10pt; line-height: 107%; font-family: Arial, sans-serif;\">Apabila tidak terdapat data pendukung, maka dibutuhkan justifikasi dari atasan sebagai alasan mengapa isu/masalah/kejadian/fakta tersebut menjadi prioritas</span><span style=\"font-family: Arial, sans-serif;\">.</span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n</body>\r\n</html>', '001/CIP-PTG/2019', 3, NULL);
