INSERT INTO `risalah_kode` VALUES ('CK4', 'ANALISIS DAMPAK POSITIF & NEGATIF', '<!DOCTYPE html>\n<html>\n<head>\n</head>\n<body>\n<ul>\n<li>Tuliskan dampak positif dan jelaskan dampak improvement jika melebihi ruang lingkup yang dikerjakan</li>\n<li>Tuliskan dampak negative dan mitigasi penanggulannnya dalam bentuk tabel</li>\n<li>Semua dampak harus didukung data yang akurat</li>\n</ul>\n<table style=\"width: 100%; border-collapse: collapse; border-color: #000000;\" border=\"1\" cellpadding=\"5\">\n<tbody>\n<tr>\n<td style=\"width: 50%;\">Dampak Positif</td>\n<td>Keterangan</td>\n</tr>\n<tr>\n<td style=\"width: 50%; vertical-align: top;\">\n<p>Jelaskan jika lebih dari satu</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</td>\n<td style=\"width: 50%; vertical-align: top;\">\n<p>Jelaskan jika hasil melebihi ruang lingkup</p>\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"width: 100%; border-collapse: collapse; border-color: #000000;\" border=\"1\" cellpadding=\"5\">\n<tbody>\n<tr>\n<td style=\"width: 50%;\">Dampak Negatif</td>\n<td>Mitigasi</td>\n</tr>\n<tr>\n<td style=\"width: 50%; vertical-align: top;\">\n<p>Jelaskan jika lebih dari satu</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</td>\n<td style=\"width: 50%; vertical-align: top;\">\n<p>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n</body>\n</html>');
