INSERT INTO `sub_bab` VALUES ('Sub 1.1', 'Identifikasi Masalah', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-left: .75in; mso-add-space: auto; text-indent: -.5in; mso-list: l0 level2 lfo1;\"><span style=\"font-family: \'Arial\',sans-serif; mso-fareast-font-family: Arial;\"><span style=\"mso-list: Ignore;\">1.1.<span style=\"font: 7.0pt \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-family: \'Arial\',sans-serif;\">IDENTIFIKASI/STRATIFIKASI ISU</span></strong></p>\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-left: 0.75in; text-indent: -0.5in; text-align: left;\"><span style=\"font-size: 10.0pt; line-height: 115%; font-family: \'Arial\',sans-serif; color: #0070c0;\">Tuliskan isu/masalah/kejadian/fakta yang akan dibahas serta lingkup tugas tim/gugus CIP yang<span style=\"mso-spacerun: yes;\">&nbsp; </span>relevan dengan Target KPI / RKAP / </span></p>\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-left: 0.75in; text-indent: -0.5in; text-align: left;\"><span style=\"font-size: 10.0pt; line-height: 115%; font-family: \'Arial\',sans-serif; color: #0070c0;\">Arahan / analisa SWOT / Visi Misi Perusahaan. Bila diperlukan tambahkan visualisasi seperti grafik, tabel, dll,</span></p>\r\n</body>\r\n</html>', 'L1');
INSERT INTO `sub_bab` VALUES ('Sub 1.2', ' Penetapan Isu/Tema Dan Kompleksitas Masalah', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p><span style=\"font-size: 10.0pt; line-height: 107%; font-family: \'Arial\',sans-serif; mso-fareast-font-family: Arial;\"><span style=\"font: 7.0pt \'Times New Roman\';\">&nbsp;<strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 10.0pt; line-height: 107%; font-family: \'Arial\',sans-serif;\">1.2&nbsp;</span></strong>&nbsp;</span></span><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-size: 10.0pt; line-height: 107%; font-family: \'Arial\',sans-serif;\">PENETAPAN ISU/TEMA DAN KOMPLEKSITAS MASALAH</span></strong></p>\r\n<ul style=\"list-style-type: square;\">\r\n<li class=\"MsoNormal\" style=\"margin-left: .25in; text-align: justify;\"><span style=\"font-size: 10pt; line-height: 107%; font-family: Arial, sans-serif;\">Bila masalah lebih dari satu, pemilihan masalah dilakukan dengan lakukan analisis prioritas (gunakan tools : misal <strong>pareto</strong>) atau dengan justifikasi </span></li>\r\n<li class=\"MsoNormal\" style=\"margin-left: .25in; text-align: justify;\"><span style=\"font-size: 10pt; line-height: 107%; font-family: Arial, sans-serif;\">Untuk justifikasi, tunjukkan KPI, Arahan Manajemen, Notulen Rapat dll sebagai justifikasi pemilihan isu/masalah/kejadian/fakta yang akan diselesaikan. Lampirkan dokumen pendukung.</span></li>\r\n<li class=\"MsoNormal\" style=\"margin-left: .25in; text-align: justify;\"><span style=\"font-size: 10pt; line-height: 107%; font-family: Arial, sans-serif;\">Apabila tidak terdapat data pendukung, maka dibutuhkan justifikasi dari atasan sebagai alasan mengapa isu/masalah/kejadian/fakta tersebut menjadi prioritas</span><span style=\"font-family: Arial, sans-serif;\">.</span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n</body>\r\n</html>', 'L1');
INSERT INTO `sub_bab` VALUES ('Sub 1.3', 'Analisis Risiko & Dampak Panca Mutu', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-left: .75in; mso-add-space: auto; text-indent: -.5in; mso-list: l0 level2 lfo1;\"><!-- [if !supportLists]--><span style=\"font-family: \'Arial\',sans-serif; mso-fareast-font-family: Arial;\"><span style=\"mso-list: Ignore;\">1.3.<span style=\"font: 7.0pt \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><!--[endif]--><strong style=\"mso-bidi-font-weight: normal;\"><span style=\"font-family: \'Arial\',sans-serif;\">ANALISIS RISIKO &amp; DAMPAK PANCA MUTU</span></strong></p>\r\n<ul>\r\n<li class=\"MsoListParagraphCxSpLast\" style=\"margin-bottom: 10.0pt; mso-add-space: auto; line-height: 115%;\"><span style=\"color: #3366ff;\">Tuliskan risk assessment matrix berdasarkan HIRADC atau ERM, jika tidak ada dapat membuat matriks sendiri. Contoh</span>&nbsp; &nbsp;<img src=\"data:image/jpeg;base64,\" alt=\"\" />&nbsp;<img src=\"data:image/jpeg;base64,\" alt=\"\" /></li>\r\n<li class=\"MsoListParagraphCxSpLast\" style=\"margin-bottom: 10.0pt; mso-add-space: auto; line-height: 115%;\"><span style=\"font-family: Arial, sans-serif; color: #0070c0;\">Mapping risiko level / dampak masalah berdasarkan aspek Panca Mutu (QCDSM) untuk penentuan prioritas penetapan masalah</span><img src=\"data:image/png;base64,\" alt=\"\" /></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n</body>\r\n</html>', 'L1');
